USE `Libregco`;
CREATE  OR REPLACE VIEW `EstadoCuentaCXPView` AS
-- Compras sin articulos
Select Compras.IDSuplidor,Suplidor.Suplidor,Compras.IDCompra,Compras.SecondID as SecondIDCompra,Compras.Fecha,Compras.Hora,Compras.IDTipoDocumento,TipoDocumento.Documento,0 as Debito,Compras.TotalNeto as Credito,Compras.TotalNeto as Pendiente,Compras.Balance from Libregco.Compras
INNER JOIN Libregco.TipoDocumento on Compras.IDTipoDocumento=TipoDocumento.IDTipoDocumento
INNER JOIN Libregco.Condicion on Compras.IDCondicion=Condicion.IDCondicion 
INNER JOIN Libregco.Suplidor on Compras.IDSuplidor=Suplidor.IDSuplidor
where Condicion.Dias>0 and Compras.IDTipoDocumento=19 AND Compras.Nulo=0 

UNION ALL

-- Compras con articulos
Select Compras.IDSuplidor,Suplidor.Suplidor,Compras.IDCompra,Compras.SecondID as SecondIDCompra,Compras.Fecha,Compras.Hora,Compras.IDTipoDocumento,TipoDocumento.Documento,0 as Debito,Compras.TotalNeto as Credito,Compras.TotalNeto as Pendiente,Compras.Balance from Libregco.Compras
INNER JOIN Libregco.TipoDocumento on Compras.IDTipoDocumento=TipoDocumento.IDTipoDocumento
INNER JOIN Libregco.Condicion on Compras.IDCondicion=Condicion.IDCondicion 
INNER JOIN Libregco.Suplidor on Compras.IDSuplidor=Suplidor.IDSuplidor
where Condicion.Dias>0 and Compras.IDTipoDocumento=6 AND Compras.Nulo=0 

UNION ALL

-- Pagos

