INNER JOIN Libregco.TipoDocumento on PagoCompra.IDTipoDocumento=TipoDocumento.IDTipoDocumento
INNER JOIN Libregco.Suplidor on PagoCompra.IDSuplidor=Suplidor.IDSuplidor
Where PagoCompra.Nulo=0


UNION ALL

-- Devoluciones

SELECT Compras.IDSuplidor,Suplidor.Suplidor,IDDevolucionCompra,DevolucionCompra.SecondID,DevolucionCompra.Fecha,DevolucionCompra.Hora,DevolucionCompra.IDTipoDocumento,TipoDocumento.Documento,Devolver,0,0,0 FROM libregco.devolucioncompra
