INNER JOIN Libregco.Compras on DevolucionCompra.IDFactura=Compras.IDCompra
INNER JOIN Libregco.Suplidor on Compras.IDSuplidor=Suplidor.IDSuplidor
Where DevolucionCompra.Nulo=0


UNION ALL

