INNER JOIN Libregco.Suplidor on NotaDebitoCreditoCXP.IDSuplidor=Suplidor.IDSuplidor
Where NotaDebitoCreditoCXP.IDTipoDocumento=22 and NotaDebitoCreditoCXP.Nulo=0

UNION ALL

-- Notas de Debito

SELECT NotaDebitoCreditoCXP.IDSuplidor,Suplidor.Suplidor,IDNotaDebitoCreditoCXP,SecondID,Fecha,Hora,NotaDebitoCreditoCXP.IDTipoDocumento,TipoDocumento.Documento,0,Neto,0,0 FROM libregco.notadebitocreditocxp
INNER JOIN Libregco.TipoDocumento on NotaDebitoCreditoCXP.IDTipoDocumento=TipoDocumento.IDTipoDocumento
INNER JOIN Libregco.Suplidor on NotaDebitoCreditoCXP.IDSuplidor=Suplidor.IDSuplidor
Where NotaDebitoCreditoCXP.IDTipoDocumento=21 and NotaDebitoCreditoCXP.Nulo=0

