DROP VIEW IF EXISTS `Libregco`.`ListasArticulosView` ;
DROP TABLE IF EXISTS `Libregco`.`ListasArticulosView`;
USE `Libregco`;
CREATE  OR REPLACE VIEW `ListasArticulosView` AS
SELECT IDListaArticulos,ListasArticulos.IDTipoDocumento,TipoDocumento.Documento,ListasArticulos.SecondID,ListasArticulos.IDUsuario,Usuarios.Nombre as NombreUsuario,ListasArticulos.Fecha,ListasArticulos.Hora,ListasArticulos.IDSucursal,Sucursal.Sucursal,ListasArticulos.IDAlmacen,Almacen.Almacen,ListasArticulos.Descripcion,ListasArticulos.Total,ListasArticulos.Nulo,listadoarticulosdetalle.IDListadoArticulosDetalle,listadoarticulosdetalle.IDArticulo,listadoarticulosdetalle.Descripcion as DescripcionArticulo,listadoarticulosdetalle.IDPrecio,listadoarticulosdetalle.IDMedida,Medida.Medida,listadoarticulosdetalle.Cantidad,listadoarticulosdetalle.Precio,listadoarticulosdetalle.Importe FROM libregco.listasarticulos
INNER JOIN Libregco.listadoarticulosdetalle on ListasArticulos.IDListaArticulos=ListadoArticulosDetalle.IDListadoArticulo
INNER JOIN Libregco.TipoDocumento on ListasArticulos.IDTipoDocumento=TipoDocumento.IDTipoDocumento
INNER JOIN Libregco.Empleados as Usuarios on ListasArticulos.IDUsuario=Usuarios.IDEmpleado
INNER JOIN Libregco.Almacen on ListasArticulos.IDAlmacen=Almacen.IDAlmacen
INNER JOIN Libregco.Sucursal on Almacen.IDSucursal=Sucursal.IDSucursal
INNER JOIN Libregco.Medida on ListadoArticulosDetalle.IDMedida=Medida.IDMedida;

