-- View `Libregco`.`EstadoClienteFactura`
-- -----------------------------------------------------
DROP VIEW IF EXISTS `Libregco`.`EstadoClienteFactura` ;
DROP TABLE IF EXISTS `Libregco`.`EstadoClienteFactura`;
USE `Libregco`;
CREATE  OR REPLACE VIEW `EstadoClienteFactura` AS
-- DETALLE ABONOS
Select Abonos.IDAbono as Clave,DetalleAbonos.IDFactura,Abonos.SecondID,Abonos.IDTipoDocumento,TipoDocumento.Documento,Abonos.IDCliente,Abonos.Fecha,Abonos.Hora,Abonos.Total,0 as Credito,Abonos.Total as Debito,DetalleAbonos.BalanceAnterior,DetalleAbonos.Cargos
from Libregco.Abonos
INNER JOIN Libregco.TipoDocumento on Abonos.IDTipoDocumento=TipoDocumento.IDTipoDocumento
INNER JOIN Libregco.DetalleAbonos on Abonos.IDAbono=DetalleAbonos.IDAbono

UNION ALL
-- FACTURADATOS
Select IDFacturaDatos as Clave,IDFacturaDatos,SecondID,FacturaDatos.IDTipoDocumento,TipoDocumento.Documento,FacturaDatos.IDCliente,FacturaDatos.Fecha,FacturaDatos.Hora,
TotalNeto,TotalNeto as Credito,0,0,0 
from Libregco.FacturaDatos 
INNER JOIN Libregco.TipoDocumento on FacturaDatos.IDTipoDocumento=TipoDocumento.IDTipoDocumento 
where FacturaDatos.IDTipoDocumento=2 or FacturaDatos.IDTipoDocumento=12 and FacturaDatos.Nulo=0 

UNION ALL
-- INICIAL
Select IDFacturaDatos as Clave,IDFacturaDatos,SecondID,FacturaDatos.IDTipoDocumento,'REGISTRO DE INICIAL',FacturaDatos.IDCliente,FacturaDatos.Fecha,DATE_ADD(FacturaDatos.Hora, INTERVAL 2 SECOND) AS Hora,
Inicial,0,Inicial as Debito,0,0 
from Libregco.FacturaDatos 
INNER JOIN Libregco.TipoDocumento on FacturaDatos.IDTipoDocumento=TipoDocumento.IDTipoDocumento 
where FacturaDatos.IDTipoDocumento=2 or FacturaDatos.IDTipoDocumento=12 and FacturaDatos.Nulo=0 


UNION ALL

-- CHEQUES DEVUELTOS
Select IDFacturaDatos as Clave,IDFacturaDatos,SecondID,FacturaDatos.IDTipoDocumento,TipoDocumento.Documento,FacturaDatos.IDCliente,FacturaDatos.Fecha,FacturaDatos.Hora,
TotalNeto,TotalNeto as Credito,0,0,0 
from Libregco.FacturaDatos 
INNER JOIN Libregco.TipoDocumento on FacturaDatos.IDTipoDocumento=TipoDocumento.IDTipoDocumento 
where FacturaDatos.IDTipoDocumento=17 and FacturaDatos.Nulo=0 

UNION ALL

-- DEVOLUCIONES
SELECT DevolucionVenta.IDDevolucionVenta,FacturaDatos.IDFacturaDatos,DevolucionVenta.SecondID,DevolucionVenta.IDTipoDocumento,TipoDocumento.Documento,FacturaDatos.IDCliente,DevolucionVenta.Fecha,DevolucionVenta.Hora,
DevolucionVenta.Devolver AS TOTAL,0,DevolucionVenta.Devolver AS DEBITO,0,0 from Libregco.DevolucionVenta 
INNER JOIN Libregco.TipoDocumento on DevolucionVenta.IDTipoDocumento=TipoDocumento.IDTipoDocumento 
INNER JOIN Libregco.FacturaDatos on DevolucionVenta.IDFactura=FacturaDatos.IDFacturaDatos 
INNER JOIN Libregco.Condicion on FacturaDatos.IDCondicion=Condicion.IDCondicion 
Where DevolucionVenta.Nulo=0 and Condicion.Dias>0 

UNION ALL

-- NOTAS DE CREDITO

SELECT NotaDebitoCredito.IDNotaDebCred,NotaDebitoCreditoDetalle.IDFactura,NotaDebitoCredito.SecondID,NotaDebitoCredito.IDTipoDocumento,TipoDocumento.Documento,NotaDebitoCredito.IDCliente,NotaDebitoCredito.Fecha,NotaDebitoCredito.Hora,NotaDebitoCreditoDetalle.Aplicado as Acreditar,0,NotaDebitoCreditoDetalle.Aplicado,BceAnterior,0
FROM libregco.notadebitocredito 
INNER JOIN Libregco.notadebitocreditodetalle on NotaDebitoCredito.IDNotaDebCred=NotaDebitoCreditoDetalle.IDNotaDebitoCredito
INNER JOIN Libregco.TipoNotaFactura on TipoNotaFactura.IDTipoNotaFactura=NotaDebitoCredito.IDTipoNota 
INNER JOIN Libregco.TipoDocumento on NotaDebitoCredito.IDTipoDocumento=TipoDocumento.IDTipoDocumento 
Where NotaDebitoCredito.IDTipoNota=2 and NotaDebitoCredito.Nulo=0

UNION ALL

-- NOTAS DE DEBITO
SELECT NotaDebitoCredito.IDNotaDebCred,NotaDebitoCreditoDetalle.IDFactura,NotaDebitoCredito.SecondID,NotaDebitoCredito.IDTipoDocumento,TipoDocumento.Documento,NotaDebitoCredito.IDCliente,NotaDebitoCredito.Fecha,NotaDebitoCredito.Hora,NotaDebitoCreditoDetalle.Aplicado as Debitar,NotaDebitoCreditoDetalle.Aplicado,0,BceAnterior,0
FROM libregco.notadebitocredito 
INNER JOIN Libregco.notadebitocreditodetalle on NotaDebitoCredito.IDNotaDebCred=NotaDebitoCreditoDetalle.IDNotaDebitoCredito
INNER JOIN Libregco.TipoNotaFactura on TipoNotaFactura.IDTipoNotaFactura=NotaDebitoCredito.IDTipoNota 
INNER JOIN Libregco.TipoDocumento on NotaDebitoCredito.IDTipoDocumento=TipoDocumento.IDTipoDocumento 
Where NotaDebitoCredito.IDTipoNota=1 and NotaDebitoCredito.Nulo=0
;
USE `Libregco_Main` ;

